import{_ as o,c as t,a2 as r,o as a}from"./chunks/framework.8rFHF1Cf.js";const s="/drone-in-a-box/assets/boxer/boxer.png",m=JSON.parse('{"title":"RadioMaster Boxer 2.4g Remote Control System","description":"","frontmatter":{},"headers":[],"relativePath":"docs/RadioMaster/description.md","filePath":"docs/RadioMaster/description.md"}'),n={name:"docs/RadioMaster/description.md"};function i(l,e,c,d,h,g){return a(),t("div",null,e[0]||(e[0]=[r('<h1 id="radiomaster-boxer-2-4g-remote-control-system" tabindex="-1">RadioMaster Boxer 2.4g Remote Control System <a class="header-anchor" href="#radiomaster-boxer-2-4g-remote-control-system" aria-label="Permalink to &quot;RadioMaster Boxer 2.4g Remote Control System&quot;">​</a></h1><h2 id="description" tabindex="-1">Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;Description&quot;">​</a></h2><p>The RadioMaster Boxer is a versatile 2.4GHz remote control system suitable for a wide range of RC models including fixed-wing aircraft, helicopters, multi-rotors, cars, boats, robotics, and more. Featuring EdgeTX firmware, the Boxer is designed for both beginners and professionals. With powerful hardware and a wide range of compatible protocols, this controller offers an intuitive setup and smooth control for all your RC needs.</p><p><img src="'+s+'" alt="RadioMaster Boxer"></p><h2 id="key-explanations" tabindex="-1">Key Explanations <a class="header-anchor" href="#key-explanations" aria-label="Permalink to &quot;Key Explanations&quot;">​</a></h2><ul><li><strong>Antenna</strong>: Ensure the antenna is installed before powering up the radio. The internal RF module will be damaged if operated without the antenna.</li><li><strong>LCD Screen</strong>: Displays all system and model information.</li><li><strong>Trim Buttons</strong>: For fine-tuning control axes.</li><li><strong>Gimbals (Left/Right)</strong>: High precision Hall gimbals for smooth control.</li><li><strong>S1, S2 Dials</strong>: Used for continuous adjustments (e.g., throttle curve, gain).</li><li><strong>SA, SB, SC, SD, SE, SF Switches</strong>: Used for various control functions (e.g., flight modes, gear control).</li><li><strong>Scroll Wheel</strong>: For easy menu navigation.</li><li><strong>Trainer Port</strong>: Used for connecting with another controller in trainer mode.</li><li><strong>USB-C Charger Port</strong>: For charging the built-in 7.4V 2-cell Li-Po battery pack.</li><li><strong>MDL Button</strong>: Access model settings.</li><li><strong>SYS Button</strong>: Access system settings, including protocol and model configuration.</li><li><strong>RTN Button</strong>: Back Button for moving out of settings.</li><li><strong>PAGE&gt; Button</strong>: To move pages front.</li><li><strong>PAGE&lt; Button</strong>: To move pages back.</li><li><strong>TELE Button</strong>: To see the drone data such as battery, flightMode,etc.</li></ul><h2 id="how-to-set-up-your-model" tabindex="-1">How to Set Up Your Model <a class="header-anchor" href="#how-to-set-up-your-model" aria-label="Permalink to &quot;How to Set Up Your Model&quot;">​</a></h2><ol><li><p><strong>Power on your Boxer transmitter</strong>: Press and hold the power button until the display turns on.</p></li><li><p><strong>Select your protocol</strong>:</p><ul><li>Press the <strong>MDL button</strong> to enter the model settings.</li><li>Scroll the <strong>Scroll Wheel</strong> to an empty option to make new Model, and press the scroll wheel to <strong>Create model</strong>.</li></ul><hr><ul><li>Press the <strong>Page&gt;</strong> button to setup the model on page 2.</li><li>Press the Scroll wheel to <strong>name the Model</strong>, and then scroll the scroll wheel to select the character [0-9,A-Z,a-z].</li><li>Note where you don&#39;t want any character and empty space just press the scroll wheel and move ahead.</li><li>After naming is completed scroll the wheel to set <strong>Internal RM Mode</strong> from <code>OFF</code> to <code>CRSF</code> by pressing and then scrolling to CRSF and then again pressing the scroll wheel.</li></ul><hr><ul><li>Go to Page 5 using <code>Page&gt;</code> button you can see the <code>INPUTS</code>.</li><li>Ail is Aileron in our case it will be Roll.</li><li>ELE is Elevator in our case it will be pitch.</li><li>Thr is Thruster in our case it will be Throttle.</li><li>Rud is Rudder in our case it will be Yaw.</li><li>These 4 paramaters are already created by default, so we will create a Arm Disarm and Flight Mode buttons.</li><li>Channel 5 is empty so press the scroll wheel and set the <code>Name</code> : ARM by using scroll wheel process is same as previous and set switch to SA.</li><li>Change Page and go to mixer can create a same option at channel 5 most of the thing will be automatically imported from INPUT channel 5.</li><li>Same way create Input -&gt; channel 6 for FM i.e Flight Modes and set the Switch to SB and create mixes same as previous step.</li></ul><hr><p>Setup a Telementry Screen.</p><ul><li>Go to page 12 and see the Screens options.</li><li>Go to Empty Space and set the <code>FM</code> option i.e Flight Mode, <code>BxTx</code> for drone battery, <code>SAT</code> for GPS Satellit count.</li></ul></li></ol><h2 id="references" tabindex="-1">References <a class="header-anchor" href="#references" aria-label="Permalink to &quot;References&quot;">​</a></h2><ul><li><a href="https://www.radiomasterrc.com/products/boxer-radio-controller-m2" target="_blank" rel="noreferrer">RadioMaster Website</a></li><li><a href="https://cdn.shopify.com/s/files/1/0609/8324/7079/files/Boxer_User_Manual.pdf?v=1712910643" target="_blank" rel="noreferrer">RadioMaster Boxer Manual</a></li></ul>',10)]))}const p=o(n,[["render",i]]);export{m as __pageData,p as default};
