import{_ as e,c as a,a2 as t,o as n}from"./chunks/framework.8rFHF1Cf.js";const u=JSON.parse('{"title":"Instructions to set up ROS 2 for the first time","description":"","frontmatter":{},"headers":[],"relativePath":"docs/swiftPro/experiments/rosSetup.md","filePath":"docs/swiftPro/experiments/rosSetup.md"}'),o={name:"docs/swiftPro/experiments/rosSetup.md"};function i(p,s,l,r,c,d){return n(),a("div",null,s[0]||(s[0]=[t(`<h1 id="instructions-to-set-up-ros-2-for-the-first-time" tabindex="-1">Instructions to set up ROS 2 for the first time <a class="header-anchor" href="#instructions-to-set-up-ros-2-for-the-first-time" aria-label="Permalink to &quot;Instructions to set up ROS 2 for the first time&quot;">​</a></h1><hr><ul><li>This document is made for ros2 humble</li><li>Ensure that system has internet connection.</li><li>Make a laptop Hotspot name <code>swiftPro</code> and set password to <code>swift123</code>.</li><li>On the raspberry Pi connect it to screen login using the credentials that we set during setup.</li><li>Ensure it is connected to hotspot and get the wlan0: inet address eg. <code>192.168.0.15</code> using the command <code>ip a</code>.</li><li>Do SSH <code>ssh swiftPro@IP-ADDRESS</code> and enter password <code>swift123</code> in another system which is connected to the swiftPro hotspot.</li><li>Run the below commnads.</li></ul><h2 id="set-locale" tabindex="-1">Set locale <a class="header-anchor" href="#set-locale" aria-label="Permalink to &quot;Set locale&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>locale  # check for UTF-8</span></span>
<span class="line"><span></span></span>
<span class="line"><span>sudo apt update &amp;&amp; sudo apt install locales</span></span>
<span class="line"><span>sudo locale-gen en_US en_US.UTF-8</span></span>
<span class="line"><span>sudo update-locale LC_ALL=en_US.UTF-8 LANG=en_US.UTF-8</span></span>
<span class="line"><span>export LANG=en_US.UTF-8</span></span>
<span class="line"><span></span></span>
<span class="line"><span>locale  # verify settings</span></span></code></pre></div><h2 id="setup-sources" tabindex="-1">Setup Sources <a class="header-anchor" href="#setup-sources" aria-label="Permalink to &quot;Setup Sources&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo apt install software-properties-common</span></span>
<span class="line"><span>sudo add-apt-repository universe</span></span></code></pre></div><h2 id="now-add-the-ros-2-gpg-key-with-apt" tabindex="-1">Now add the ROS 2 GPG key with apt. <a class="header-anchor" href="#now-add-the-ros-2-gpg-key-with-apt" aria-label="Permalink to &quot;Now add the ROS 2 GPG key with apt.&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo apt update &amp;&amp; sudo apt install curl -y</span></span>
<span class="line"><span>sudo curl -sSL https://raw.githubusercontent.com/ros/rosdistro/master/ros.key -o /usr/share/keyrings/ros-archive-keyring.gpg</span></span></code></pre></div><h2 id="then-add-the-repository-to-your-sources-list" tabindex="-1">Then add the repository to your sources list. <a class="header-anchor" href="#then-add-the-repository-to-your-sources-list" aria-label="Permalink to &quot;Then add the repository to your sources list.&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>echo &quot;deb [arch=$(dpkg --print-architecture) signed-by=/usr/share/keyrings/ros-archive-keyring.gpg] http://packages.ros.org/ros2/ubuntu $(. /etc/os-release &amp;&amp; echo $UBUNTU_CODENAME) main&quot; | sudo tee /etc/apt/sources.list.d/ros2.list &gt; /dev/null</span></span></code></pre></div><h2 id="install-ros-2-packages" tabindex="-1">Install ROS 2 packages <a class="header-anchor" href="#install-ros-2-packages" aria-label="Permalink to &quot;Install ROS 2 packages&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo apt update &amp;&amp; sudo apt upgrade</span></span></code></pre></div><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo apt install ros-humble-ros-base</span></span></code></pre></div><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo apt install ros-dev-tools</span></span></code></pre></div><h2 id="environment-setup" tabindex="-1">Environment setup <a class="header-anchor" href="#environment-setup" aria-label="Permalink to &quot;Environment setup&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>nano ~/.bashrc</span></span></code></pre></div><ul><li>Go to end of the file and paste the below path.</li></ul><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>source /opt/ros/humble/setup.bash</span></span></code></pre></div><ul><li>And you are set to go.</li></ul>`,20)]))}const g=e(o,[["render",i]]);export{u as __pageData,g as default};
